mapper.select:
SELECT file_url
FROM et_file
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND file_role = #{role}
  AND deleted_at IS NULL
ORDER BY file_id ASC
LIMIT 1

SELECT file_url
FROM et_file
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND deleted_at IS NULL
ORDER BY file_id ASC

mapper.update:
UPDATE et_file
SET deleted_at = NOW()
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND deleted_at IS NULL
