mapper.sql:
WHERE 1=1


  AND u.email LIKE CONCAT('%', #{req.userEmail}, '%')



  AND o.order_code LIKE CONCAT('%', #{req.orderCode}, '%')



  AND o.order_status = #{req.status}



  AND o.total_amount >= #{req.minTotalAmount}



  AND o.total_amount <= #{req.maxTotalAmount}



  AND o.shipped_at >= #{req.shippedFrom}



  AND o.shipped_at <= #{req.shippedTo}



  AND o.delivered_at >= #{req.deliveredFrom}



  AND o.delivered_at <= #{req.deliveredTo}

mapper.update:
UPDATE et_order
SET order_status = #{status}
WHERE order_id = #{orderId}
