mapper.sql:
WHERE 1=1


  AND p.post_type = #{req.postType}



  AND LOWER(u.email) LIKE CONCAT('%', LOWER(#{req.userEmail}), '%')



  AND LOWER(p.title) LIKE CONCAT('%', LOWER(#{req.keyword}), '%')



  AND p.created_at >= #{req.createdFrom}



  AND p.created_at <= #{req.createdTo}


AND p.deleted_at IS NULL
