mapper.sql:
WHERE u.deleted_at IS NULL


  AND u.email LIKE CONCAT('%', #{req.email}, '%')



  AND u.name LIKE CONCAT('%', #{req.name}, '%')



  AND u.status = #{req.status}



  AND u.provider = #{req.provider}



  AND u.created_at >= #{req.createdFrom}



  AND u.created_at <= #{req.createdTo}

mapper.update:
UPDATE et_user
SET
  status = #{status}
WHERE user_id = #{userId}
  AND deleted_at IS NULL
