mapper.select:
SELECT file_url
FROM et_file
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND file_role = #{fileRole}
  AND deleted_at IS NULL
ORDER BY file_id ASC
LIMIT 1

SELECT file_url
FROM et_file
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND file_role = #{fileRole}
  AND deleted_at IS NULL
ORDER BY file_id ASC

SELECT f.ref_id AS refId,
       f.file_url AS fileUrl
FROM et_file f
JOIN (
  SELECT ref_id, MIN(file_id) AS file_id
  FROM et_file
  WHERE ref_type = #{refType}
    AND file_role = #{fileRole}
    AND deleted_at IS NULL
    AND ref_id IN

      #{id}

  GROUP BY ref_id
) t ON t.file_id = f.file_id

mapper.update:
UPDATE et_file
SET deleted_at = NOW()
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND deleted_at IS NULL

UPDATE et_file
SET deleted_at = NOW()
WHERE ref_type = #{refType}
  AND ref_id = #{refId}
  AND file_role = #{fileRole}
  AND deleted_at IS NULL
