SELECT p.product_id, p.category_id, c.name AS category_name, p.name, p.description, p.base_price AS min_sku_price, p.average_rating, p.review_count, p.status, p.created_at FROM et_product p JOIN et_category c ON c.category_id = p.category_id
LEFT JOIN ( SELECT s.product_id, COALESCE(SUM(oi.quantity - oi.cancelled_quantity), 0) AS sold_qty FROM et_order_item oi JOIN et_order o ON o.order_id = oi.order_id JOIN et_product_sku s ON s.sku_id = oi.sku_id WHERE o.order_status IN ('PAID', 'PARTIALLY_CANCELLED', 'SHIPPING', 'DELIVERED') GROUP BY s.product_id ) sales ON sales.product_id = p.product_id
WHERE p.deleted_at IS NULL AND p.status IN ('ON_SALE', 'SOLD_OUT')
AND p.category_id IN ( SELECT c2.category_id FROM et_category c2 WHERE c2.category_id = #{categoryId} OR c2.parent_id = #{categoryId} )
AND p.name LIKE CONCAT('%', #{keyword}, '%')
AND p.base_price >= #{minPrice}
AND p.base_price <= #{maxPrice}
AND p.average_rating >= #{minRating}
AND EXISTS ( SELECT 1 FROM et_product_discount d WHERE d.product_id = p.product_id AND (d.start_at IS NULL OR d.start_at <= NOW()) AND (d.end_at IS NULL OR d.end_at >= NOW()) )
ORDER BY
p.created_at DESC, p.product_id DESC COALESCE(sales.sold_qty, 0) DESC, p.product_id DESC p.base_price ASC p.base_price DESC p.average_rating DESC, p.product_id DESC p.review_count DESC, p.product_id DESC p.name ASC p.created_at DESC p.product_id DESC
LIMIT #{size} OFFSET #{offset}

SELECT
  COUNT(*)
FROM et_product p
JOIN et_category c ON c.category_id = p.category_id
WHERE p.deleted_at IS NULL
  AND p.status IN ('ON_SALE', 'SOLD_OUT')


  AND p.category_id IN (
    SELECT c2.category_id
    FROM et_category c2
    WHERE c2.category_id = #{categoryId}
       OR c2.parent_id = #{categoryId}
  )



  AND p.name LIKE CONCAT('%', #{keyword}, '%')



  AND p.base_price >= #{minPrice}



  AND p.base_price <= #{maxPrice}



  AND p.average_rating >= #{minRating}



  AND EXISTS (
    SELECT 1
    FROM et_product_discount d
    WHERE d.product_id = p.product_id
      AND (d.start_at IS NULL OR d.start_at <= NOW())
      AND (d.end_at IS NULL OR d.end_at >= NOW())
  )

